mapper.select:
SELECT oji.offer_id offerId, oi.project_name projectName, oi.check_type checkType, oji.`status`, oi.consignor_name consignorName, oi.finish_date finishDate, oi.check_task_count checkTaskCount, oi.create_user_id createUserId, oi.remark, oi.update_time createTime FROM t_offer_judge_info oji LEFT JOIN t_offer_info oi ON oi.id = oji.offer_id
and oi.organ_id = #{organId,jdbcType=VARCHAR}
and oji.offer_id = #{sv.offerId,jdbcType=VARCHAR}
and oji.status = #{sv.status,jdbcType=VARCHAR}
and oi.project_name like concat('%',#{sv.projectName,jdbcType=VARCHAR},'%')
order by oi.update_time desc

SELECT oji.offer_id offerId, oji.`status` status FROM t_offer_judge_info oji LEFT JOIN t_offer_info oi ON oji.offer_id = oi.id WHERE oi.organ_id = #{organId}
AND YEAR ( oji.create_time ) = YEAR ( NOW() )
AND DATE_FORMAT( oji.create_time, '%Y%m' ) = DATE_FORMAT( CURDATE(), '%Y%m' )
