#{item}
GROUP BY empName ORDER BY cnt DESC

SELECT su.emp_name empName, count( sid.sample_item_id ) cnt FROM t_sample_item_data sid LEFT JOIN t_sample_item sii ON sid.sample_item_id = sii.id LEFT JOIN t_sys_user su ON sid.collect_user_id = su.user_id WHERE sii.organ_id = #{organId} AND YEAR ( sii.create_time ) = YEAR ( NOW() ) AND sii.sample_status IN
#{item}
GROUP BY empName ORDER BY cnt DESC;

