select ssa.id storeApplyId, sii.id sampItemId, sii.sample_no sampleNo, sii.factor_point factorPoint, sii.secd_class_name secdClassName, sii.create_time createTime, sii.sample_status sampStatus, sii.store_flag storeFlag, sid.review_user_id reviewUserId, sj.id jobId, sj.start_date startDate, sj.end_date endDate, sj.inspection_name inspectionName, sj.project_name projectName, su.emp_name collectUser from t_sample_store_apply ssa left join t_sample_item sii on ssa.sample_item_id = sii.id left join t_sample_item_data sid on sii.id = sid.sample_item_id left join t_sample_task sti on sii.sample_task_id = sti.id left join t_schedule_job sj on sti.job_id = sj.id left join t_sys_user su on sid.collect_user_id = su.user_id where sii.organ_id = #{organId} and ssa.approval_status = 0
and sii.id = #{sv.sampItemId}
and sj.id = #{sv.jobId}
order by ssa.create_time desc, sii.sample_no asc

