mapper.select:
SELECT st.id taskId, st.latest_schedule_date latestScheduleDate, st.plan_num planNum, st.scheduled_plan_num scheduledPlanNum, st.create_time createTime, st.schedule_flag scheduleFlag, st.cont_code contCode, oi.project_name projectName, oi.consignor_name consignorName, oi.consignor_linker consignorLinker, oi.consignor_linker_phone consignorLinkerPhone FROM t_schedule_task st LEFT JOIN t_offer_info oi ON st.offer_id = oi.id WHERE st.del_status = '0' and oi.organ_id = #{organId}
AND st.schedule_status = #{sv.scheduleStatus}
AND st.schedule_status in (0, 1)
AND st.cont_code = #{sv.contCode}
AND oi.project_name like concat('%',#{sv.projectName},'%')
ORDER BY st.create_time DESC

SELECT COUNT(*) FROM t_schedule_job WHERE organ_id = #{organId} and job_status != 0 and del_status = 0
AND YEAR ( create_time ) = YEAR ( NOW() )
AND DATE_FORMAT( create_time, '%Y%m' ) = DATE_FORMAT( CURDATE(), '%Y%m' );
